// import React,{Component} from "react";
import {useState,useEffect, useRef} from 'react';
import { nanoid } from 'nanoid';

import Form from "./Form";
import ContactsList from "./ContactsList";
import Filter from "./Filter/Filter";
import Section from "components/Section";
import {Box} from '../Box/Box';


const PhoneBook = () =>{
    const [filter, setFilter] = useState('');
    const [contacts, setContacts] = useState([]);

    const firstRender = useRef(true);

    useEffect(()=>{
        if(firstRender.current){
            const contacts = localStorage.getItem("contacts");
            const parsedContacts = JSON.parse(contacts);
            // parsedContacts?.length like default value.
            if(parsedContacts?.length){
                setContacts(parsedContacts)
            };
        firstRender.current = false;
        };
    },[])

    useEffect(()=>{
        if(!firstRender.current){
            localStorage.setItem('contacts', JSON.stringify(contacts))
        };
    },[contacts])

    const addContact = data => {
        const dublicate = contacts.find(contact=> contact.name === data.name)
        if(dublicate){
            alert(`${data.name} is already in name list`);
        }
        setContacts(prevState => {
            const { name, number } = data;
            const newContact = {
              name,
              number,
              id: nanoid(),
            };
            return [...prevState, newContact];
          });
    };

    const deleteContacts = (id) => {
        setContacts(prevState =>
            prevState.filter(contact => contact.id !== id)
        );
    };

    const getFilteredContacts = () =>{
        const filterText = filter.toLowerCase();
        const filterContacts = contacts.filter(({ name }) => {
        const result = name.toLowerCase().includes(filterText);
            return result
        })
    return filterContacts;
    };

    const changeFilter = ({target}) =>{
        setFilter( target.value )
    };

    return(
        <Box
        ml='20px'
        mt='20px'>
            <Section title="PhoneBook">
                <Box
                border={'normal'}
                paddingLeft='8px'
                paddingRight='40px'
                paddingTop='8px'
                paddingBottom="20px">
                    <Form onSubmit={addContact}/>
                </Box>
            </Section>
            <Section title='Contacts'>
                <Box
                    paddingLeft='8px'
                    paddingRight='40px'
                    paddingTop='8px'
                    paddingBottom="40px">
                    <Filter filter={filter} changeFilter={changeFilter}/>
                    <ContactsList contacts={getFilteredContacts()} deleteContacts={deleteContacts}/>
                </Box>
            </Section>
        </Box>
    )
};
export default PhoneBook;

